import Link from 'next/link'
import { useState, useEffect } from 'react'
import {phoneNumber} from '../../components/layout'

import initFirebase from '../../components/initFirebase'
import firebase from "firebase/app"
import 'firebase/auth'
require('firebase/auth')
require("firebase/firestore")

initFirebase()
var db = firebase.firestore()

function NewEvent (props) {
    const [display, setDisplay] = useState(false)
    const [year, setYear] = useState(2020)
    const [month, setMonth] = useState("")
    const [monthError, setMonthError] = useState(false)
    const [day, setDay] = useState("")
    const [dayError, setDayError] = useState(false)
    const [title, setTitle] = useState("")
    const [titleError, setTitleError] = useState(false)
    const [place, setPlace] = useState("")
    const [placeError, setPlaceError] = useState(false)
    const [address, setAddress] = useState("")
    const [addressError, setAddressError] = useState(false)
    const [mensPrice, setMensPrice] = useState("")
    const [mensPriceError, setMensPriceError] = useState(false)
    const [womensPrice, setWomensPrice] = useState("")
    const [womensPriceError, setWomensPriceError] = useState(false)
    const [mensRequirement, setMensRequiremens] = useState("")
    const [mensRequirementError, setMensRequiremensError] = useState(false)
    const [womensRequirement, setWomensRequirement] = useState("")
    const [womensRequirementError, setWomensRequirementError] = useState(false)

    const judgeSubmit = () => {
        if (!month) {
            setMonthError(true)
        } else {
            setMonthError(false)
        }
        if (!day) {
            setDayError(true)
        } else {
            setDayError(false)
        }
    }

    const toggleDisplay = () => {
        setDisplay(!display)
    }


    var yearList = [];
    for (var i=0; i<5; i++) {
        yearList.push(<option key={i.toString()} value={2020+i}>{2020+i}</option>);
    }
    var monthList = [];
    for (var i=0; i<12; i++) {
        monthList.push(<option key={i.toString()} value={1+i}>{1+i}</option>);
    }
    var dayList = [];
    for (var i=0; i<31; i++) {
        dayList.push(<option key={i.toString()} value={1+i}>{1+i}</option>);
    }
    return (
        <div>
            <div className={display ? "w-full h-full fixed bg-white z-10 inset-0 overflow-auto" : "hidden"}>
                <div className="flex fixed top-0 right-0 h-10 justify-center items-center z-30 px-6">
                    <img className="h-4 w-4" 
                    src="/images/cross-icon.png"
                    onClick={toggleDisplay}
                    />
                </div>
                <div className="flex flex-col p-4 my-8 justify-center items-center">
                    <input className="border rounded p-2 w-full outline-none my-2 max-w-sm" placeholder="タイトル"/>
                    <div className="flex w-full max-w-sm justify-between">
                        <div className="w-1/3 ">
                            <select onChange={e => setYear(e.target.value)} className="rounded border outline-none p-2 w-full">
                                {yearList}
                            </select>
                        </div>
                        <div className="w-1/3 ">
                            <select 
                                onChange={e => setMonth(e.target.value)} 
                                className={monthError ? "rounded border outline-none p-2 w-full border-red-500" : "rounded border outline-none p-2 w-full"}>
                                {monthList}
                            </select>
                        </div>
                        <div className="w-1/3 ">
                            <select onChange={e => setDay(e.target.value)} className="rounded border outline-none p-2 w-full">
                                {dayList}
                            </select>
                        </div>
                    </div>
                    <input placeholder="場所" className="border rounded p-2 w-full outline-none my-2 max-w-sm"/>
                    <input placeholder="住所" className="border rounded p-2 w-full outline-none my-2 max-w-sm"/>
                    <div className="border rounded p-2 w-full outline-none my-2 flex max-w-sm">
                        <input placeholder="男性料金" className="w-4/5"/>
                        <div className="w-1/5">円</div>
                    </div>
                    <div className="border rounded p-2 w-full outline-none my-2 flex max-w-sm">
                        <input placeholder="女性料金" className="w-4/5"/>
                        <div className="w-1/5">円</div>
                    </div>
                    <input placeholder="男性募集状況" className="border rounded p-2 w-full outline-none my-2 max-w-sm"/>
                    <input placeholder="女性募集状況" className="border rounded p-2 w-full outline-none my-2 max-w-sm"/>
                    <input placeholder="男性年齢制限" className="border rounded p-2 w-full outline-none my-2 max-w-sm"/>
                    <input placeholder="女性年齢制限" className="border rounded p-2 w-full outline-none my-2 max-w-sm"/>
                    <textarea className="border rounded p-2 w-full outline-none max-w-sm">内容</textarea>
                    <input type="button" value="作成" className="border rounded p-2 outline-none my-2 bg-white"/>
                </div>
            </div>
            <div className="flex justify-center align-center py-2">
                <input type="button" value="新規" onClick={toggleDisplay} className="p-2 bg-white rounded border cursor-pointer outline-none"/>
            </div>
        </div>

    )
}

function Event (props) {
    return (
        <div className="list-none border-t-2 py-4">
            <Link href="/posts/[id]" as={`/posts/${props.event.id}`}>
                <a><h3 className="text-blue-600 text-center">{props.event.title}</h3></a>
            </Link>
            <p className="text-center">{props.event.date}</p>
